import React, { useState } from 'react';

export const Select = ({ children, onValueChange, defaultValue }) => {
  const [selectedValue, setSelectedValue] = useState(defaultValue);

  const handleChange = (value) => {
    setSelectedValue(value);
    if (onValueChange) {
      onValueChange(value);
    }
  };

  return (
    <div className="relative">
      {React.Children.map(children, (child) =>
        React.cloneElement(child, { selectedValue, onChange: handleChange })
      )}
    </div>
  );
};

export const SelectTrigger = ({ children, className }) => (
  <button
    className={`bg-gray-200 rounded-lg px-4 py-2 text-left w-full ${className}`}
  >
    {children}
  </button>
);

export const SelectContent = ({ children }) => (
  <div className="absolute z-10 mt-2 bg-white rounded-lg shadow-md w-full">
    {children}
  </div>
);

export const SelectItem = ({ value, children, onChange = () => {}, selectedValue }) => (
  <div
    className={`px-4 py-2 cursor-pointer hover:bg-gray-100 ${
      selectedValue === value ? "bg-gray-100" : ""
    }`}
    onClick={() => onChange && onChange(value)} // onChange가 있는 경우에만 실행
  >
    {children}
  </div>
);

export const SelectValue = ({ placeholder, selectedValue }) => (
  <span className="text-gray-700">
    {selectedValue || placeholder || "Select an option"}
  </span>
);
